import { useState } from "react";

const StateName = (props) => {
  const [visibility, setVisibility] = useState(false);
  const stateClick = () => {
    setVisibility(!visibility);
  };
  return (
    <>
    <div>
      <h2 onClick={stateClick} id={'city'+props.id}>{props.item.name}</h2>
      
        {visibility
          ? <ul> {props.item.towns.map((item, index) => {
              return <li key={index} id={'town'+(index+1)}>{item.name}</li>;
            })}</ul>
          : null}
      </div>
    </>
  );
};

export default StateName;
