import React,{useState} from "react";
import "./App.css";

const App = () => {
  
  const [apiLinks, setApiLinks] = useState([]);
  const [apiLinksTest, setApiLinksTest] = useState([]);

  function api(a){
    fetch(`https://en.wikipedia.org/w/api.php?&origin=*&format=json&action=opensearch&search=${a}`)
    .then(data => data.json())
    .then(res => {
      setApiLinks(res[3]);
      setApiLinksTest(res[1]);
    })
  }

  var timer ;
  const debouncing = (e) => {
    clearTimeout(timer);
    timer = setTimeout(()=>{
      console.log(e.target.value)
      api(e.target.value)
      console.log(apiLinksTest)
    },2000)
  }
  return (
    <div className="container">
      <h1 >Wiki Search</h1>
      <input type="text" onChange={debouncing} />
      {
        // apiLinks && apiLinksTest.map((item,index) => {
        //   return<>
        //    {/* <a href={apiLinks[index]} key={index}>{item[index]}</a>
        //     <br/> */}
        //     <li>{item}</li>
        //   </>
        // })
      }
    </div>
  );
};

export default App;
