import React,{useState} from "react";
import "./App.css";

const App = () => {
  
  const [apiLinks, setApiLinks] = useState([]);
  const [apiLinksTest, setApiLinksTest] = useState([]);

  function api(a){
    fetch(`https://en.wikipedia.org/w/api.php?&origin=*&format=json&action=opensearch&search=${a}`)
    .then(data => data.json())
    .then(res => {
      setApiLinks(res[3]);
      setApiLinksTest(res[1]);
    })
  }

  var timer ;
  const debouncing = (e) => {
    clearTimeout(timer);
    timer = setTimeout(()=>{
      api(e.target.value)
    },300)
  }
  return (
    <div className="container">
      <h1 >Wiki Search</h1>
      <input type="text" onChange={debouncing} /><br/>
      {
        apiLinks && apiLinksTest.map((item,index) => {
          
          return<div key={index}>
            {item ?
           <a href={apiLinks[index]}>{item}</a> : null}
          </div>
        })
      }
    </div>
  );
};

export default App;
