import { useState } from "react";
import CitiesName from "./CitiesName";

const StateName = (props) => {
  const [visibility, setVisibility] = useState(false);
  const stateClick = () => {
    setVisibility(!visibility);
  };
  return (
    <>
      <h1 onClick={stateClick} id={'state'+props.id}>{props.item.name}</h1>
      
        {visibility
          ?<ul>{ props.item.cities.map((item, index) => {
              return <CitiesName key={index} item={item} id={index+1} />;
            })}</ul>
          : null}
    </>
  );
};

export default StateName;
