import React,{useState} from "react";
import "./App.css";

const App = () => {
  
  const [text, setText] = useState();
  const [apiLinks, setApiLinks] = useState();
  const [apiLinksTest, setApiLinksTest] = useState();

  function api(a){
    fetch(`https://en.wikipedia.org/w/api.php?&origin=*&format=json&action=opensearch&search=${a}`)
    .then(data => data.json())
    .then(res => {
      console.log(res);
      setApiLinks(apiLinks => res[3]);
      setApiLinksTest(apiLinksTest => res[1]);
    })
  }

  var timer ;
  const debouncing = (e) => {
    clearTimeout(timer);
    timer = setTimeout(()=>{

      setText(e.target.value);
      api(text)
    },2000)
  }
  return (
    <div className="container">
      <h1 >Wiki Search</h1>
      <input type="text" onChange={debouncing} />
      {
        apiLinks && apiLinksTest.map((item,index) => {
          <>
           <a href="#" key={index}>{item}</a>
            <br/>
          </>
        })
      }
    </div>
  );
};

export default App;
